import React, { useEffect, useState } from "react";
import Loader from "react-loader-spinner";
import { usePromiseTracker } from "react-promise-tracker";
import "./loading.css";

const Loading = () => {
  const [data, setData] = useState([]);
  const [input, setInput] = useState("");
  const [city, setCity] = useState("");
  const handleClick = (e) => {
    return setCity(e.target.value);
  };
  useEffect(() => {
    fetch(`https://vast-shore-74260.herokuapp.com/banks?city=${city}`)
      .then((res) => res.json())
      .then((result) => setData(result));
  }, [city]);

  const { promiseInProgress } = usePromiseTracker();

  return (
    promiseInProgress && (
      <div className="loader">
        <div className="homepage">
          <h1 style={{ marginLeft: "25px" }}>Bank Branches</h1>
          <div className="topnav">
            <div className="dropdown">
              <select onClick={handleClick} className="dropbtn">
                <option>MUMBAI</option>;<option>DELHI</option>;
                <option>AHMEDABAD</option>;<option>CHENNAI</option>;
                <option>BANGLORE</option>;
              </select>
            </div>
            <input
              type="text"
              placeholder="Search.."
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
          </div>

          <Loader
            type="ThreeDots"
            color="rgba(244, 149, 149, 0.8)"
            width="100"
            height="100"
          />
        </div>
      </div>
    )
  );
};

export default Loading;
