import React, { useEffect, useState } from "react";
import TableData from "./datatable/index";
import "./HomePage.css";
import { Link, useParams } from "react-router-dom";

function HomePage() {
  const [data, setData] = useState([]);
  const [input, setInput] = useState("");
  const [city, setCity] = useState("");

  // useEffect(() => {
  //   fetch(`https://vast-shore-74260.herokuapp.com/banks?city=${city}`)
  //     .then((response) => response.json())
  //     .then((json) => setData(json));

  // }, []);

  useEffect(() => {
    fetch(`https://vast-shore-74260.herokuapp.com/banks?city=${city}`)
      .then((res) => res.json())
      .then((result) => setData(result));
  }, [city]);

  const {cities} = useParams();
  console.log(cities);
  const handleClick = (e) => {
    return setCity(e.target.value);
  };

  return (
    <div className="homepage">
      <h1 style={{ marginLeft: "25px" }}>Bank Branches</h1>
      <div className="topnav">
        <div className="dropdown">
          <button className="dropbtn">Cities</button>
          <div className="dropdown-content">
            <a onClick={handleClick} value={MUMBAI}>Mumbai</a>
            <a onClick={handleClick} value={MUMBAI}>Delhi</a>
            <a onClick={handleClick} value={MUMBAI}>Ahemdabad</a>
            <a onClick={handleClick} value={MUMBAI}>Bangalore</a>
            <a onClick={handleClick} value={MUMBAI}>Chennai</a>
          {/* <select onClick={handleClick} className="dropbtn">
            <Link to={`/banks/${city}`}><option>MUMBAI</option></Link>;
           <Link to={`/banks/${city}`}><option>DELHI</option></Link>;
           <Link to={`/banks/${city}`}><option>AHMEDABAD</option></Link>;
           <Link to={`/banks/${city}`}><option>CHENNAI</option></Link>;
           <Link to={`/banks/${city}`}><option>BANGLORE</option></Link>;
          </select> */}
          </div>
        </div>
        <input
          type="text"
          placeholder="Search.."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
      </div>
      <div className="table">
        <TableData data={data} />
      </div>
    </div>
  );
}

export default HomePage;
