import React, { useEffect, useState } from "react";
import TableData from "./datatable/index";
import "./BanksList.css"
import {Link , useParams} from 'react-router-dom';


function BanksList() {
  const [data, setData] = useState([]);
  const [input, setInput] = useState("");
  
 
  
  useEffect(() => {
    fetch("https://vast-shore-74260.herokuapp.com/banks?city=AHEMDABAD")
      .then((response) => response.json())
      .then((json) => setData(json));

  }, []);

const handleClick = () => {
    return(
<div></div>
    )
}

  return (
    <div className="banks_list">
      
      <h1 style={{ marginLeft: "25px" }}><Link to="/" style={{ textDecoration:"none",  }}>{`<`}</Link> Bank Branches</h1>
      <div className="topnav">
        <div className="dropdown">
          <button className="dropbtn">Cities</button>
          <div className="dropdown-content">
            <a href="/banks/mumbai">Mumbai</a>
            <a href="/banks/delhi">Delhi</a>
            <a href="/banks/ahemdabad" >Ahemdabad</a>
            <a href="/banks/bangalore">Bangalore</a>
            <a href="/banks/chennai">Chennai</a>
          </div>
        </div>
        <input
          type="text"
          placeholder="Search.."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
      </div>
      <div className="table">
        <TableData data={data} />
      </div>
    </div>
  );
}

export default BanksList;
