import React, { useEffect, useState } from "react";
import TableData from "./datatable/index";
import "./HomePage.css";
import Loading from './Loading';
import { trackPromise } from 'react-promise-tracker';

function HomePage() {
  const [data, setData] = useState([]);
  const [input, setInput] = useState("");
  const [city, setCity] = useState("");
  const [ searchColumns, setSearchColumns] = useState("");


  useEffect(() => {
    fatchdata ();
    
  }, [city]);

  const fatchdata = () => {
    trackPromise(
    fetch(`https://vast-shore-74260.herokuapp.com/banks?city=${city}`)
    .then((res) => res.json())
    .then((result) => setData(result))
    );
  }

  const handleClick = (e) => {
    return setCity(e.target.value);
  };

  function search(data){
    const columns = data[0] && Object.keys(data[0]);
    return data.filter(
      (row) =>
        columns.some(
          (column) => row[column].toString().toLowerCase().indexOf(input.toLowerCase()) > -1
        )

    );
  }

  return (
    <div className="homepage">
      <h1 style={{ marginLeft: "25px" }}>Bank Branches</h1>
      <div className="topnav">
        <div className="dropdown">
          <select onClick={handleClick} className="dropbtn">
           <option >MUMBAI</option>;
           <option >DELHI</option>;
           <option >AHMEDABAD</option>;
           <option >CHENNAI</option>;
           <option >BANGLORE</option>;
          </select>
        </div>
        <input
          type="text"
          placeholder="Search.."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
      </div>
      <div className="table">
        <TableData data={search(data)} />
        <Loading />
      </div>
    </div>
  );
}

export default HomePage;
