import React, { useEffect, useState } from "react";
import TableData from "./datatable/index";
import "./HomePage.css"
import {Link , useParams} from 'react-router-dom';


function HomePage() {
  const [data, setData] = useState([]);
  const [input, setInput] = useState("");
  const [city ,setCity] = useState("");
 
  
  useEffect(() => {
    fetch(`https://vast-shore-74260.herokuapp.com/banks?city=${city}`)
      .then((response) => response.json())
      .then((json) => setData(json));
     
  }, []);

  const handleChange = (e) => {
    setCity({
        [e.currentTarget.name]: e.currentTarget.value,
    });
};

  return (
    <div className="homepage">
      <h1 style={{ marginLeft: "25px" }}>Bank Branches</h1>
      <div className="topnav">
        <div className="dropdown">
          <button className="dropbtn">Cities</button>
          <div className="dropdown-content">
            <a name="MUMBAI" value={city} onClick={() =>handleChange(this.name)}>Mumbai</a>
            <a name="DELHI" value={city}  onClick={() =>handleChange(this.name)}>Delhi</a>
            <a name="AHEMDABAD" value={city} onClick={() =>handleChange(this.name)}>Ahemdabad</a>
            <a name="BANGALORE" value={city} onClick={() =>handleChange(this.name)}>Bangalore</a>
            <a name="CHENNAI" value={city} onClick={() =>handleChange(this.name)}>Chennai</a>
            
          </div>
        </div>
        <input
          type="text"
          placeholder="Search.."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
      </div>
      <div className="table">
        <TableData data={data}/>
      </div>
    </div>
  );
}

export default HomePage;
