import React, { useEffect, useState } from "react";
import TableData from "./datatable/index";
import "./HomePage.css";


function HomePage() {
  const [data, setData] = useState([]);
  const [input, setInput] = useState("");
  const [city, setCity] = useState("");



  useEffect(() => {
    fetch(`https://vast-shore-74260.herokuapp.com/banks?city=${city}`)
      .then((res) => res.json())
      .then((result) => setData(result));
  }, [city]);

  const handleClick = (e) => {
    return setCity(e.target.value);
  };

  function search(data){
    return data.filter(
      (row) =>
        row.ifsc.indexOf(input) > -1 

    );
  }

  return (
    <div className="homepage">
      <h1 style={{ marginLeft: "25px" }}>Bank Branches</h1>
      <div className="topnav">
        <div className="dropdown">
          <select onClick={handleClick} className="dropbtn">
           <option >MUMBAI</option>;
           <option >DELHI</option>;
           <option >AHMEDABAD</option>;
           <option >CHENNAI</option>;
           <option >BANGLORE</option>;
          </select>
        </div>
        <input
          type="text"
          placeholder="Search.."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
      </div>
      <div className="table">
        <TableData data={search(data)} />
      </div>
    </div>
  );
}

export default HomePage;
