import React, { useEffect, useState } from "react";
import TableData from "./datatable/index";
import "./HomePage.css"
import {Link , useParams} from 'react-router-dom';


function HomePage() {
  const [data, setData] = useState([]);
  const [input, setInput] = useState("");

 
  
  useEffect(() => {
    fetch("https://vast-shore-74260.herokuapp.com/banks?city=AHEMDABAD")
      .then((response) => response.json())
      .then((json) => setData(json));
     
  }, []);

  return (
    <div className="homepage">
      <h1 style={{ marginLeft: "25px" }}>Bank Branches</h1>
      <div className="topnav">
        <div className="dropdown">
          <button className="dropbtn">Cities</button>
          <div className="dropdown-content">
            <Link to={`/banks/${city}`} city="mumbai" >Mumbai</Link>
            <Link to={`/banks/${city}`}>Delhi</Link>
            <Link to="/ahemdabad">Ahemdabad</Link>
            <Link to="/bangalore">Bangalore</Link>
            <Link to="/chennai">Chennai</Link>
          </div>
        </div>
        <input
          type="text"
          placeholder="Search.."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
      </div>
      <div className="table">
        <TableData data={data}/>
      </div>
    </div>
  );
}

export default HomePage;
